import * as React from 'react';
import { addEffect, addAfterEffect } from '@react-three/fiber';

function useIntersect(onChange) {
  var ref = React.useRef(null);
  var check = React.useRef(false);
  var temp = React.useRef(false);
  React.useEffect(function () {
    var obj = ref.current;

    if (obj) {
      // Stamp out frustum check pre-emptively
      var unsub1 = addEffect(function () {
        check.current = false;
        return true;
      }); // If the object is inside the frustum three will call onRender

      var oldOnRender = obj.onBeforeRender;

      obj.onBeforeRender = function () {
        return check.current = true;
      }; // Compare the check value against the temp value, if it differs set state


      var unsub2 = addAfterEffect(function () {
        if (check.current !== temp.current) onChange(temp.current = check.current);
        return true;
      });
      return function () {
        obj.onBeforeRender = oldOnRender;
        unsub1();
        unsub2();
      };
    }
  }, []);
  return ref;
}

export { useIntersect };
